        return new Twig_Source("{% extends \"FOSUserBundle::layout.html.twig\" %}

{% trans_default_domain 'FOSUserBundle' %}

{% block title %}{{ parent() }} : Réinitialisation de votre mot de passe{% endblock %}

{% block meta %}{{ parent() }}
\t\t<meta name=\"description\" content=\"Vous avez oublié votre mot de passe sur &laquo;Ma petite tirelire&raquo; ? Ne vous inquiétez pas :  vous pouvez le réinitialiser.\" />
\t\t<meta name=\"robots\" content=\"noindex, nofollow\" />
{% endblock %}

{% block panel_heading %}<h2>Réinitialisation <small>de votre mot de passe</small></h2>{% endblock %}

{% block panel_body %}
\t<p class=\"text-justify\">
\t\tPour réinitialiser votre mot de passe, veuillez saisir votre nom d'utilisateur ou votre adresse e-mail.
\t</p>
\t<p class=\"text-justify\">
\t\tUn mail vous sera ensuite envoyé vous indiquant la marche à suivre. 
\t</p>
\t{% if invalid_username is defined %}
\t\t<div class=\"alert alert-danger alert-dismissible fade in\" role=\"alert\">
\t\t\t<button type=\"button\" class=\"close\" data-dismiss=\"alert\" aria-label=\"Close\"><span aria-hidden=\"true\">×</span></button>
\t\t\t<span class=\"glyphicon glyphicon-warning-sign\"></span> {{ 'resetting.request.invalid_username'|trans({'%username%': invalid_username}) }}
\t\t</div>
\t{% endif %}
\t
\t<form action=\"{{ path('fos_user_resetting_send_email') }}\"\" method=\"POST\" class=\"fos_user_resetting_request\" style=\"margin-top:20px;\">
\t    <div>
\t        
\t        {#<label for=\"username\">{{ 'resetting.request.username'|trans }}</label>#}
\t        <input type=\"text\" id=\"username\" name=\"username\" required=\"required\" class=\"form-control\" placeholder=\"{{ 'resetting.request.username'|trans }}\"/>
\t    </div>
\t    
\t    <div>
\t        <input type=\"submit\" id=\"_submit\" value=\"{{ 'resetting.request.submit'|trans }}\" class=\"btn btn-primary btn-block\" />
\t    </div>
\t</form>
{% endblock %}

{% block panel_footer %}
<a href={{ path('fos_user_security_login') }}>Retour à la page de connexion.</a>
{% endblock %}
", "FOSUserBundle:Resetting:request.html.twig", "C:\\wamp64\\www\\GestionComptes-master\\src\\FGS\\UserBundle/Resources/views/Resetting/request.html.twig");
